import React, {useState} from 'react';
import ReactDOM from 'react-dom'

const Button = (props) => {
  return(
    <button onClick={props.handlClick}>{props.text}</button>
  )
}

const Display = (props) => {
  return(
    <div>{props.response}: {props.count}</div>
  )
}

const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const clickGood = () =>{
    setGood( good + 1)
  }

  return (
    <div>
      <h1>give feeedback</h1>
      <Button handlClick={clickGood} text="good" />
      <Button handlClick={clickNeutral} text="neutral" />
      <Button handlClick="clickBad" text="bad" />

      <h1>Statistics</h1>
      <Display response="good" count={good} />
      <Display response="neutral" count={neutral} />
      <Display response="bad" count={bad} />
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
